try {
    const debug = env.get("debug");
    const storeType = env.get("store_type") || "file";
    const storeType2 = "memoryOnly";
    // based on selected devices for the scenario
    // read devices status in memory
    let devicesSelection = [];
    global.get("devicesSelection", storeType, (err,res) => {
        if (err) throw err;
        devicesSelection = res;
    });
    let devicesStatus = true;
    if (debug) {
        console.log("setDevicesStatus:req", devicesSelection);
    }
    if (!devicesSelection) {
        global.set("devicesSelection", [], storeType, err => {
            if (err) throw err;
        });
        devicesStatus = true;
    } else if (devicesSelection.length < 1) {
        devicesStatus = true;
    } else {
        const validators = devicesSelection.map(deviceName => {
            let device;
            global.get(`device-${deviceName}`, storeType2, (err, res) => {
                if (err) throw err;
                device = res;
            });
            if (device && device.status) {
                return true;
            }
            return false;
        });
        if (validators.some(v => v === false)) {
            devicesStatus = false;
        } else {
            devicesStatus = true;
        }
    }
    if (debug) {
        console.log("setDevicesStatus:res", devicesStatus);
    }
    global.set("devicesStatus", devicesStatus, storeType, err => {
        if (err) throw err;
    });
    msg.devicesStatus = devicesStatus;
    return msg;
} catch(error){
    return error;
}

